INSERT INTO `data_tagline` VALUES (1,10,'Pengumuman Masuk Sekolah','<p><strong>Informasi Penting:</strong> Siswa-siswi untuk pemberangkatan dan pembelajaran sudah dimulai pada <u>tanggal 15 Juli 2025</u>. Harap hadir tepat waktu dan mematuhi seluruh peraturan sekolah yang berlaku.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(2,5,'Kesiapan Pembelajaran Tatap Muka','<p><strong>Perhatian!</strong> Pembelajaran tatap muka akan dilaksanakan secara penuh mulai <u>15 Juli 2025</u>. Siswa diharapkan membawa perlengkapan sekolah secara lengkap.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(3,10,'Selamat Datang Tahun Ajaran Baru','<p>Selamat datang di tahun ajaran baru! <br> Kegiatan belajar mengajar akan dimulai <u>15 Juli 2025</u>. Mari kita awali semangat baru dengan disiplin dan tanggung jawab.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(4,10,'Apel Pagi Wajib','<p><i>Reminder:</i> Seluruh siswa diwajibkan mengikuti apel pagi sebelum kegiatan belajar mengajar yang dimulai <u>15 Juli 2025</u>. Hadir tepat waktu sangat dihargai.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(5,6,'Jadwal Masuk Tahun Ajaran Baru','<p><strong>Jadwal Masuk:</strong> Tahun ajaran baru akan dimulai <u>15 Juli 2025</u>. Mohon untuk mengecek jadwal masing-masing dan hadir sesuai ketentuan yang berlaku.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(6,10,'Informasi Pemberangkatan','<p><strong>Informasi Penting:</strong> Siswa-siswi untuk pemberangkatan dan pembelajaran sudah dimulai pada <u>tanggal 15 Juli 2025</u>. Harap hadir tepat waktu dan mematuhi seluruh peraturan sekolah yang berlaku.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(7,9,'Persiapan Masuk Sekolah','<p><strong>Perhatian!</strong> Pembelajaran tatap muka akan dilaksanakan secara penuh mulai <u>15 Juli 2025</u>. Siswa diharapkan membawa perlengkapan sekolah secara lengkap.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(8,8,'Awal Semester Baru','<p>Selamat datang di tahun ajaran baru! <br> Kegiatan belajar mengajar akan dimulai <u>15 Juli 2025</u>. Mari kita awali semangat baru dengan disiplin dan tanggung jawab.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(9,7,'Kedisiplinan Siswa','<p><i>Reminder:</i> Seluruh siswa diwajibkan mengikuti apel pagi sebelum kegiatan belajar mengajar yang dimulai <u>15 Juli 2025</u>. Hadir tepat waktu sangat dihargai.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(10,7,'Konfirmasi Jadwal Masuk','<p><strong>Jadwal Masuk:</strong> Tahun ajaran baru akan dimulai <u>15 Juli 2025</u>. Mohon untuk mengecek jadwal masing-masing dan hadir sesuai ketentuan yang berlaku.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(11,4,'Orientasi Siswa Baru','<p><strong>Catatan:</strong> Proses orientasi siswa baru (MOS) akan dilaksanakan selama 3 hari pertama mulai <u>15 Juli 2025</u>.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(12,5,'Agenda Hari Pertama','<p><strong>Agenda Hari Pertama:</strong> Siswa diminta hadir pukul 06.45 WIB untuk apel dan pembagian kelas di lapangan utama sekolah.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(13,6,'Pembagian Buku Pelajaran','<p><i>Update:</i> Pembagian buku pelajaran dilakukan setelah pembelajaran hari pertama selesai. Pastikan membawa tas tambahan.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(14,10,'Aturan Seragam','<p><strong>Seragam:</strong> Hari pertama masuk sekolah menggunakan seragam putih abu-abu lengkap dengan atribut sekolah.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(15,4,'Motivasi Awal Tahun','<p>Selamat belajar! Tahun ajaran baru dimulai dengan semangat baru. Gunakan kesempatan ini untuk meraih prestasi terbaikmu!</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(16,9,'Perlengkapan Hari Pertama','<p><i>Penting:</i> Jangan lupa membawa alat tulis lengkap dan menjaga kebersihan kelas masing-masing sejak hari pertama.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(17,9,'Pertemuan Wali Murid','<p><strong>Info Orang Tua:</strong> Diharapkan hadir dalam pertemuan wali murid yang akan dilaksanakan <u>18 Juli 2025</u>.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(18,5,'Pendaftaran Ekstrakurikuler','<p><strong>Pendaftaran Ekstrakurikuler</strong> dibuka mulai <u>20 Juli 2025</u>. Pilih kegiatan yang sesuai dengan minatmu!</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(19,9,'Sosialisasi Kurikulum','<p><strong>Sosialisasi Kurikulum</strong> akan dilaksanakan pada minggu kedua pembelajaran. Jadwal menyusul melalui wali kelas.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(20,7,'Aturan Kendaraan Siswa','<p><i>Pengingat:</i> Siswa tidak diperkenankan membawa kendaraan pribadi tanpa izin resmi dari sekolah.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(21,4,'Jam Belajar Harian','<p><strong>Jam Belajar:</strong> Dimulai pukul 07.00 hingga 12.15. Jadwal pelajaran dapat diakses melalui papan pengumuman.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(22,7,'Penerapan Protokol Kesehatan','<p><strong>Himbauan:</strong> Terapkan protokol kesehatan di lingkungan sekolah, termasuk cuci tangan dan jaga jarak.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(23,5,'Informasi OSIS untuk Siswa Baru','<p>Untuk siswa baru, silakan menghubungi OSIS untuk informasi lebih lanjut mengenai pengenalan sekolah.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(24,9,'Pelajaran Olahraga','<p><i>Note:</i> Pelajaran olahraga akan dimulai minggu kedua. Siswa diminta membawa pakaian olahraga sesuai jadwal.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00'),(25,9,'Penilaian Awal Semester','<p><strong>Penilaian Awal</strong> akan dilaksanakan pada minggu ke-4. Persiapkan diri dari sekarang dan konsultasikan jika ada kendala belajar.</p>',1,'2025-09-10 17:00:00','2025-09-10 20:50:00');
